create database users;
use users;
create table profile (id INT NOT NULL AUTO_INCREMENT PRIMARY KEY,  surname TEXT, name TEXT, age TEXT,  height FLOAT, phone TEXT, imported TEXT);
select surname , name , age ,  height, phone from profile where imported like '114215072020';
/**********************************************************************************************/
create database liga;
use liga;
create table Clubes (chave INT NOT NULL AUTO_INCREMENT PRIMARY KEY, Clube TEXT, Cidade TEXT, Estadio TEXT, Lotacao INT, imported TEXT);
create table Vencedores (chave INT NOT NULL AUTO_INCREMENT PRIMARY KEY, Ano INT, Campeao TEXT, imported TEXT);
select Clube, Cidade, Estadio, Lotacao from Clubes where imported like '114215072020';
select Ano, Campeao from Vencedores where imported like '114215072020';
